// Helper function to get translation with interpolation
export const tBs = (key, params = {}) => {
  const keys = key.split('.')
  let value = translationsBs
  
  for (const k of keys) {
    if (value && typeof value === 'object' && k in value) {
      value = value[k]
    } else {
      return key // Return key if translation not found
    }
  }
  
  if (typeof value === 'string') {
    return value.replace(/\{(\w+)\}/g, (match, param) => {
      return params[param] !== undefined ? params[param] : match
    })
  }
  
  return key
}

// Helper function to get translation with pluralization
export const tPluralBs = (key, count, params = {}) => {
  const translation = tBs(key, { ...params, count })
  
  if (typeof translation === 'string') {
    return translation.replace(/\{plural\}/g, count === 1 ? '' : 's')
  }
  
  return translation
}

export default translationsBs
